    Item(id: 300, name: "Aries", imagePath: "assets/images/Aries.png", desc: "Ramming speed", effect: "+0.25 Speed. Running into enemies while at a high speed will deal damage. Prevents Isaac from taking contact damage while moving at high speed."),
    Item(id: 301, name: "Cancer", imagePath: "assets/images/Cancer.png", desc: "HP up + you feel protected", effect: "Adds 3 Soul Hearts. Grants The Wafer effect for the current room after taking damage."),
    Item(id: 302, name: "Leo", imagePath: "assets/images/Leo.png", desc: "Stompy", effect: "Isaac can destroy rocks by walking into them."),
