    Item(id: 337, name: "Broken Watch", imagePath: "assets/images/Broken Watch.png", desc: "I think it's broken", effect: "Slows all enemies in every fourth newly-discovered room, with a small chance to speed them up instead."),
    Item(id: 338, name: "The Boomerang", imagePath: "assets/images/The Boomerang.png", desc: "It will never leave you", effect: "Throws a boomerang which retrieves pickups, as well as damages and petrifies enemies hit for 2 seconds."),
    Item(id: 339, name: "Safety Pin", imagePath: "assets/images/Safety Pin.png", desc: "Evil + range + shot speed up", effect: "Grants one Black Heart, +2.5 range, +0.16 shot speed."),
    Item(id: 340, name: "Caffeine Pill", imagePath: "assets/images/Caffeine Pill.png", desc: "Speed up + size down", effect: "Grants +0.3 speed, reduces Isaac's size, and gives a random pill."),
    Item(id: 341, name: "Torn Photo", imagePath: "assets/images/Torn Photo.png", desc: "Tears + shot speed up", effect: "+0.7 tears, +0.16 shot speed."),
