    Item(id: 352, name: "Glass Cannon (fixed)", imagePath: "assets/images/Glass Cannon (fixed).png", desc: "Be gentle...", effect: "Fires a high-damage piercing spectral tear. If Isaac takes damage while holding this, he takes extra damage and it becomes Broken Glass Cannon."),
    Item(id: 353, name: "Bomber Boy", imagePath: "assets/images/Bomber Boy.png", desc: "Cross blast + 5 bombs", effect: "+5 Bombs. Isaac's bombs will now explode in a large cross-shaped pattern (roughly 5 bombs explosions wide). This also affects player-spawned Troll Bombs."),
    Item(id: 354, name: "Crack Jacks", imagePath: "assets/images/Crack Jacks.png", desc: "HP up. Don't swallow the prize!", effect: "Grants one full Red Heart container and drops a random trinket. Heals 1 additional Red Heart."),
