    Item(id: 371, name: "Curse of the Tower", imagePath: "assets/images/Curse of the Tower.png", desc: "Embrace chaos", effect: "Upon taking damage, spawns six Troll Bombs around the room, similar to the effect of XVI - The Tower."),
    Item(id: 372, name: "Charged Baby", imagePath: "assets/images/Charged Baby.png", desc: "Bbbzzzzzt!", effect: "Spawns a familiar that occasionally drops a Lil' Battery, gives an active item charge, or petrifies all enemies in the room."),
    Item(id: 373, name: "Dead Eye", imagePath: "assets/images/Dead Eye.png", desc: "Accuracy brings power!", effect: "Isaac's damage increases each time an enemy is hit. Damage is reset upon missing a shot."),
    Item(id: 374, name: "Holy Light", imagePath: "assets/images/Holy Light.png", desc: "Holy death shot", effect: "Adds a chance to fire holy tears, which will create a damaging light beam in the area if they hit an enemy."),
    Item(id: 375, name: "Host Hat", imagePath: "assets/images/Host Hat.png", desc: "Blast resistance", effect: "A host familiar that sits on Isaac's head and has a random chance to block enemy shots and retaliate with 3 tears. Grants immunity to explosions."),
    Item(id: 376, name: "Restock", imagePath: "assets/images/Restock.png", desc: "Never ending stores!", effect: "Shops to instantly restock their items when they are bought. Outside of Greed Mode, the restocked item's cost is increased after each purchase."),
    Item(id: 377, name: "Bursting Sack", imagePath: "assets/images/Bursting Sack.png", desc: "Spider love", effect: "Spider enemies no longer target or deal contact damage to Isaac."),
    Item(id: 378, name: "Number Two", imagePath: "assets/images/Number Two.png", desc: "Uh oh...", effect: "Continuously firing tears for three seconds causes Isaac to drop a Butt Bomb, which damages and confuses all enemies in the room when it explodes."),
    Item(id: 379, name: "Pupula Duplex", imagePath: "assets/images/Pupula Duplex.png", desc: "Wide shot", effect: "Gives Isaac spectral tears and gives his tears a wide arc shape, increasing their size."),
    Item(id: 380, name: "Pay to Play", imagePath: "assets/images/Pay to Play.png", desc: "Money talks", effect: "+5 Coins. All locked doors, Locked Chests, and Key Blocks must be opened with one Penny instead of Keys."),
    Item(id: 381, name: "Eden's Blessing", imagePath: "assets/images/Eden's Blessing.png", desc: "Tears up + your future shines brighter", effect: "+0.7 tears up. Grants a random item at the start of the next run."),
    Item(id: 382, name: "Friendly Ball", imagePath: "assets/images/Friendly Ball.png", desc: "Gotta fetch 'em all!", effect: "Throwing the ball at a monster instantly kills and captures it. The captured enemy can later be released and will be friendly, fighting for Isaac and persisting between rooms."),
    Item(id: 383, name: "Tear Detonator", imagePath: "assets/images/Tear Detonator.png", desc: "Remote tear detonation!", effect: "Splits all of Isaac's tears that are currently in the air into 6 tears that fire in all directions, which retain their damage and shot speed."),
    Item(id: 384, name: "Lil Gurdy", imagePath: "assets/images/Lil Gurdy.png", desc: "A gurd of your own!", effect: "A Gurdy Jr. familiar that charges up while Isaac is firing, bouncing around the room when released and dealing contact damage based on its speed."),
    Item(id: 385, name: "Bumbo", imagePath: "assets/images/Bumbo.png", desc: "Bumbo want coin!", effect: "Spawns a beggar familiar which picks up any nearby coins, leveling up after collecting several coins. At level 2, Bumbo gains a body. At level 3, it begins to fire tears. At level 4, Bumbo follows enemies and gains contact damage."),
    Item(id: 386, name: "D12", imagePath: "assets/images/D12.png", desc: "Rerolls rocks", effect: "Re-rolls most types of Obstacles in the current room into other random obstacles."),
    Item(id: 387, name: "Censer", imagePath: "assets/images/Censer.png", desc: "Peace be with you", effect: "Spawns a familiar which creates a circular aura that slows down enemies and enemy shots inside."),
    Item(id: 388, name: "Key Bum", imagePath: "assets/images/Key Bum.png", desc: "He wants your keys!", effect: "Spawns a familiar that follows Isaac and collects Keys for himself, giving random Chests in return."),
    Item(id: 389, name: "Rune Bag", imagePath: "assets/images/Rune Bag.png", desc: "Rune generator", effect: "Spawns a familiar that drops a random rune after clearing enough rooms."),
    Item(id: 390, name: "Seraphim", imagePath: "assets/images/Seraphim.png", desc: "Sworn friend", effect: "Spawns an angel familiar who follows Isaac and fires homing tears which deal 10 damage and have 75% shot speed."),
    Item(id: 391, name: "Betrayal", imagePath: "assets/images/Betrayal.png", desc: "Turn your enemy", effect: "Enemies are able to damage each other. If an enemy is damaged by another enemy, they will attack that enemy instead of Isaac."),
    Item(id: 392, name: "Zodiac", imagePath: "assets/images/Zodiac.png", desc: "The heavens will change you", effect: "Gives Isaac a random zodiac item effect that changes every floor."),
    Item(id: 393, name: "Serpent's Kiss", imagePath: "assets/images/Serpent's Kiss.png", desc: "The kiss of death", effect: "Tears have a chance to apply a poison effect to enemies. Isaac poisons enemies on contact. Poisoned enemies have a chance to drop a Black Heart when killed."),
    Item(id: 394, name: "Marked", imagePath: "assets/images/Marked.png", desc: "Directed tears", effect: "Isaac will automatically fire tears directed at a red target on the ground. The target can be moved with tear controls. +0.7 tears, increases range."),
    Item(id: 395, name: "Tech X", imagePath: "assets/images/Tech X.png", desc: "Laser ring tears", effect: "Tears are replaced with laser rings that pierce through enemies and deal damage to enemies within them. Can be charged to increase the effectiveness of the ring."),
    Item(id: 396, name: "Ventricle Razor", imagePath: "assets/images/Ventricle Razor.png", desc: "Short cutter", effect: "Creates up to two portals that remain even if Isaac leaves the room. Upon entering a portal, Isaac is teleported to the other portal."),
    Item(id: 397, name: "Tractor Beam", imagePath: "assets/images/Tractor Beam.png", desc: "Controlled tears", effect: "Isaac's tears are bound to a beam emitting in the direction he's firing. \n +1 Fire Rate, +2.5 Range, +0.16 Shot Speed. "),
    Item(id: 398, name: "God's Flesh", imagePath: "assets/images/God's Flesh.png", desc: "Shrink shot!", effect: "Tears have a chance to shrink enemies, making them run away from Isaac. Shrunk enemies can be crushed and killed by walking over them."),
    Item(id: 399, name: "Maw of the Void", imagePath: "assets/images/Maw of the Void.png", desc: "Consume thy enemy!", effect: "Isaac can charge and unleash a black ring that rapidly deals damage around him."),
    Item(id: 400, name: "Spear of Destiny", imagePath: "assets/images/Spear of Destiny.png", desc: "Your destiny", effect: "In addition to tears, Isaac holds a spear in front of him which deals contact damage equal to 2Ã— his tear damage, and occasionally inflicts fear."),
    Item(id: 401, name: "Explosivo", imagePath: "assets/images/Explosivo.png", desc: "Sticky bomb shot!", effect: "Isaac's tears have a chance to stick to enemies and explode."),
    Item(id: 402, name: "Chaos", imagePath: "assets/images/Chaos.png", desc: "!!!", effect: "Drops 1-6 random pickups on the floor. Causes items to be chosen from random item pools."),
    Item(id: 403, name: "Spider Mod", imagePath: "assets/images/Spider Mod.png", desc: "Mod buddy!", effect: "Displays Isaac's tear damage and enemy health bars. Spawns a familiar that wanders around and inflicts random status effects on enemies it touches. Can spawn a Lil' Battery/Blue spider upon clearing a room."),
    Item(id: 404, name: "Farting Baby", imagePath: "assets/images/Farting Baby.png", desc: "He farts!", effect: "Spawns a familiar that follows Isaac and blocks enemy shots. If a shot hits Farting Baby, it has a chance to fart, which charms, poisons, or knocks back nearby enemies."),
    Item(id: 405, name: "GB Bug", imagePath: "assets/images/GB Bug.png", desc: "Double tap glitch", effect: "Spawns a familiar that follows Isaac. Double tapping the fire button launches it forward, causing it to reroll the first monster or pickup it hits."),
    Item(id: 406, name: "D8", imagePath: "assets/images/D8.png", desc: "Reroll stats", effect: "Re-rolls Isaac's damage, tears, range, and speed."),
    Item(id: 407, name: "Purity", imagePath: "assets/images/Purity.png", desc: "Aura stat boost", effect: "Boosts one of Isaac's stats depending on the color of the aura around him. The aura disappears upon taking damage and reappears with a random color upon entering a new room. Red boosts damage, blue boosts tears, yellow boosts speed, and orange boosts range."),
    Item(id: 408, name: "Athame", imagePath: "assets/images/Athame.png", desc: "Call to the void", effect: "After Isaac kills an enemy, adds a luck-based chance to spawn a damaging black ring around them."),
    Item(id: 409, name: "Empty Vessel", imagePath: "assets/images/Empty Vessel.png", desc: "I reward an empty vessel", effect: "Grants two Black Hearts. While Isaac has no red hearts, grants flight and periodically grants an invincibility shield for a few seconds."),
    Item(id: 410, name: "Evil Eye", imagePath: "assets/images/Evil Eye.png", desc: "Eye shot!", effect: "Adds a chance to fire a slow-moving eye that fires tears in the same direction as Isaac. The eye is destroyed when it hits an enemy or obstacle."),
    Item(id: 411, name: "Lusty Blood", imagePath: "assets/images/Lusty Blood.png", desc: "Their blood brings rage!", effect: "Isaac's damage increases for the current room each time he kills an enemy."),
    Item(id: 412, name: "Cambion Conception", imagePath: "assets/images/Cambion Conception.png", desc: "Feed them hate", effect: "After taking damage a certain amount of times, a permanent demonic familiar spawns."),
    Item(id: 413, name: "Immaculate Conception", imagePath: "assets/images/Immaculate Conception.png", desc: "Feed them love", effect: "Upon picking up a certain amount of health, spawn a permanent angelic familiar."),
    Item(id: 414, name: "More Options", imagePath: "assets/images/More Options.png", desc: "There's options", effect: "Two item pedestals are presented in every Treasure Room. Only one can be taken; the other will disappear."),
    Item(id: 415, name: "Crown of Light", imagePath: "assets/images/Crown of Light.png", desc: "The untainted gain power", effect: "+2 Soul Hearts. While Isaac has no empty red hearts, grants 2x damage, increased range, and -0.3 shot speed. Regardless of health, the crown deactivates for the current room when Isaac takes damage."),
    Item(id: 416, name: "Deep Pockets", imagePath: "assets/images/Deep Pockets.png", desc: "More money!", effect: "Increases the coin cap to 999. If a room would otherwise yield no reward, 1-3 coins will spawn."),
    Item(id: 417, name: "Succubus", imagePath: "assets/images/Succubus.png", desc: "Damage booster", effect: "Spawns a flying familiar that bounces around the room with a black aura, dealing rapid damage to any enemies inside it. While standing in the aura, Isaac gains a 1.5Ã— tear damage multiplier."),
    Item(id: 418, name: "Fruit Cake", imagePath: "assets/images/Fruit Cake.png", desc: "Rainbow effects!", effect: "Isaac's tears gain a random effect that changes every shot."),
    Item(id: 419, name: "Teleport 2.0", imagePath: "assets/images/Teleport 2.0.png", desc: "I-Teleport!", effect: "Teleports Isaac to a room that has not been explored yet. Normal rooms are prioritized over special rooms, and common special rooms take priority over rarer ones."),
    Item(id: 420, name: "Black Powder", imagePath: "assets/images/Black Powder.png", desc: "Spin the black circle!", effect: "Isaac leaves a trail of black powder on the floor. Walking in a circle will spawn a pentagram symbol on the floor, which deals 10 damage per tick to any enemies inside it."),
    Item(id: 421, name: "Kidney Bean", imagePath: "assets/images/Kidney Bean.png", desc: "Love toots!", effect: "Isaac farts, charming all nearby enemies."),
    Item(id: 422, name: "Glowing Hourglass", imagePath: "assets/images/Glowing Hourglass.png", desc: "Turn back time", effect: "Rewinds time, putting Isaac back in the previous room and undoing anything that happened in the current room. Can only be used three times per floor, after which it functions like The Hourglass until the next floor."),
    Item(id: 423, name: "Circle of Protection", imagePath: "assets/images/Circle of Protection.png", desc: "Protect me from myself", effect: "Surrounds Isaac with a large white halo that occasionally deals Isaac's tear damage to enemies inside it, and can convert enemy shots into friendly homing tears."),
    Item(id: 424, name: "Sack Head", imagePath: "assets/images/Sack Head.png", desc: "More sacks!", effect: "Spawns a Grab Bag on pickup. Coins, Bombs, Keys, cards, runes and batteries have a chance to become Grab Bags."),
    Item(id: 425, name: "Night Light", imagePath: "assets/images/Night Light.png", desc: "Scared of the dark?", effect: "A cone of light shines in the direction Isaac is moving that slows enemies and enemy shots. "),
    Item(id: 426, name: "Obsessed Fan", imagePath: "assets/images/Obsessed Fan.png", desc: "Follows my every move...", effect: "Spawns a familiar that follows Isaac's movements on a delay and deals 30 contact damage per second to enemies."),
    Item(id: 427, name: "Mine Crafter", imagePath: "assets/images/Mine Crafter.png", desc: "Booom!", effect: "Places a Movable TNT barrel that can be pushed and used to destroy rocks or damage enemies. If used again in the same room while the previous TNT barrel is still intact, remotely detonates the TNT."),
    Item(id: 428, name: "PJs", imagePath: "assets/images/PJs.png", desc: "You feel cozy", effect: "Grants 4 Soul Hearts and fully restores Isaac's health."),
    Item(id: 429, name: "Head of the Keeper", imagePath: "assets/images/Head of the Keeper.png", desc: "Penny tears!", effect: "Isaac's tears become coins. Hitting an enemy has a 5% chance to drop a Penny."),
    Item(id: 430, name: "Papa Fly", imagePath: "assets/images/Papa Fly.png", desc: "Turret follower", effect: "Spawns a familiar that follows Isaac's movements on a 3 second delay, firing tears at nearby enemies."),
    Item(id: 431, name: "Multidimensional Baby", imagePath: "assets/images/Multidimensional Baby.png", desc: "ydduB Buddy", effect: "Spawns a familiar that will follow Isaac's movements on a 2.5-second delay. Tears that pass through the baby will be doubled and increase in speed."),
    Item(id: 432, name: "Glitter Bombs", imagePath: "assets/images/Glitter Bombs.png", desc: "Prize bombs!", effect: "+5 Bombs. Bombs have a chance to drop random pickups or charm enemies when they explode."),
    Item(id: 433, name: "My Shadow", imagePath: "assets/images/My Shadow.png", desc: "Me! And my shaaaadow!", effect: "Spawns a shadow familiar that closely follows behind Isaac. It spawns a black friendly Charger whenever an enemy touches it."),
    Item(id: 434, name: "Jar of Flies", imagePath: "assets/images/Jar of Flies.png", desc: "Bug catcher", effect: "Killing an enemy adds a fly to the jar, up to a maximum of 20. On use, releases Blue Flies equal to the number of flies in the jar."),
    Item(id: 435, name: "Lil Loki", imagePath: "assets/images/Lil Loki.png", desc: "4-way buddy!", effect: "Spawns a Loki familiar that follows Isaac and fires 4 tears in a cross pattern."),
    Item(id: 436, name: "Milk!", imagePath: "assets/images/Milk!.png", desc: "Don't cry over it...", effect: "Spawns a glass familiar that spills on the floor upon taking damage. After the milk spills, Isaac's tear delay is decreased by 2 for the current room. After the milk spills, Isaac's fire rate is increased by 1 for the current room."),
    Item(id: 437, name: "D7", imagePath: "assets/images/D7.png", desc: "Roll again", effect: "Restarts the current room and brings back all enemies, allowing more rewards to be acquired by completing the room."),
    Item(id: 438, name: "Binky", imagePath: "assets/images/Binky.png", desc: "Tears up", effect: "Grants one Soul Heart, +0.75 tears and decreases Isaac's size."),
    Item(id: 439, name: "Mom's Box", imagePath: "assets/images/Mom's Box.png", desc: "What's inside?", effect: "Increases luck by 1 and doubles the effects of trinkets while held. When used, drops a random trinket on the ground."),
    Item(id: 440, name: "Kidney Stone", imagePath: "assets/images/Kidney Stone.png", desc: "Edmund's kidney stone", effect: "Isaac is occasionally forced to stop firing and charge an attack that launches a massive piercing, spectral kidney stone and a barrage of tears."),
    Item(id: 441, name: "Mega Blast", imagePath: "assets/images/Mega Blast.png", desc: "Laser breath", effect: "Isaac fires a giant laser for approximately 15 seconds. This laser deals massive damage, pushes Isaac backwards, and persists between rooms and floors."),
    Item(id: 442, name: "Dark Prince's Crown", imagePath: "assets/images/Dark Prince's Crown.png", desc: "Loss is power", effect: "When at exactly one Red Heart, +2 fire rate, +1.5 range, and +0.2 shot speed."),
    Item(id: 443, name: "Apple!", imagePath: "assets/images/Apple!.png", desc: "Trick or treat?", effect: "+0.3 tears. Adds a chance to fire a razor blade that deals 4Ã— Isaac's damage."),
    Item(id: 444, name: "Lead Pencil", imagePath: "assets/images/Lead Pencil.png", desc: "He's a bleeder!", effect: "After every 15 tears fired, Isaac will fire a high-damage cluster of tears."),
    Item(id: 445, name: "Dog Tooth", imagePath: "assets/images/Dog Tooth.png", desc: "Bark at the moon!", effect: "+0.3 damage, +0.1 speed. Plays a howl or bark sound when entering a room with undiscovered Secret Rooms or Crawl Spaces."),
    Item(id: 446, name: "Dead Tooth", imagePath: "assets/images/Dead Tooth.png", desc: "Toxic breath", effect: "While firing, Isaac gains a green aura that poisons nearby enemies."),
    Item(id: 447, name: "Linger Bean", imagePath: "assets/images/Linger Bean.png", desc: "Crying makes me toot", effect: "Constantly shooting causes Isaac to release gas clouds that can be pushed by his tears and damage nearby enemies."),
    Item(id: 448, name: "Shard of Glass", imagePath: "assets/images/Shard of Glass.png", desc: "Blood and guts!", effect: "Taking damage causes Isaac to bleed, spraying a volley of blood in the direction he fires. Bleeding will eventually damage him, but it can be stopped by picking up a Red Heart."),
    Item(id: 449, name: "Metal Plate", imagePath: "assets/images/Metal Plate.png", desc: "It itches...", effect: "Grants one Soul Heart. Enemy shots have a chance to be reflected at the shooter as confusing tears."),
    Item(id: 450, name: "Eye of Greed", imagePath: "assets/images/Eye of Greed.png", desc: "Gold tears!", effect: "Every 20 tears, Isaac loses 1 Penny and shoots an additional gold tear that petrifies enemies and turns them into gold. Any enemy killed while in this state will drop coins."),
    Item(id: 451, name: "Tarot Cloth", imagePath: "assets/images/Tarot Cloth.png", desc: "I see the future", effect: "Drops a random card or rune. Doubles the effects of tarot cards."),
    Item(id: 452, name: "Varicose Veins", imagePath: "assets/images/Varicose Veins.png", desc: "I'm leaking...", effect: "Taking damage releases a ring of 10 high damage tears."),
    Item(id: 453, name: "Compound Fracture", imagePath: "assets/images/Compound Fracture.png", desc: "Bone tears!", effect: "Increases Range. Tears turn into bones, which shatter into 1-3 bone shards upon hitting an enemy or obstacle."),
    Item(id: 454, name: "Polydactyly", imagePath: "assets/images/Polydactyly.png", desc: "Hold me!", effect: "Allows Isaac to carry two cards, pills, or any combination of both. Spawns one random card, rune, or pill."),
    Item(id: 455, name: "Dad's Lost Coin", imagePath: "assets/images/Dad's Lost Coin.png", desc: "I remember this...", effect: "+2.5 range. Drops a Lucky Penny."),
    Item(id: 456, name: "Midnight Snack", imagePath: "assets/images/Midnight Snack.png", desc: "HP up", effect: "Grants one full Red Heart container. Heals 1 additional Red Heart."),
    Item(id: 457, name: "Cone Head", imagePath: "assets/images/Cone Head.png", desc: "Hard headed!", effect: "Grants a Soul Heart. Gives Isaac a chance to ignore any damage taken."),
    Item(id: 458, name: "Belly Button", imagePath: "assets/images/Belly Button.png", desc: "What's in there?", effect: "Drops a random trinket. Grants a second trinket slot."),
    Item(id: 459, name: "Sinus Infection", imagePath: "assets/images/Sinus Infection.png", desc: "Booger tears!", effect: "Adds a chance to fire a booger that sticks to enemies and deals damage over time."),
    Item(id: 460, name: "Glaucoma", imagePath: "assets/images/Glaucoma.png", desc: "Blind tears!", effect: "Adds a chance to shoot tears that confuse enemies for an extremely long time."),
    Item(id: 461, name: "Parasitoid", imagePath: "assets/images/Parasitoid.png", desc: "Egg tears!", effect: "Adds a chance to fire an egg sack which creates slowing creep and spawns a Blue Fly or Blue Spider upon hitting an enemy."),
    Item(id: 462, name: "Eye of Belial", imagePath: "assets/images/Eye of Belial.png", desc: "Possessed tears!", effect: "Grants piercing tears. After piercing one enemy, tears start homing and deal double damage."),
    Item(id: 463, name: "Sulfuric Acid", imagePath: "assets/images/Sulfuric Acid.png", desc: "Acid tears!", effect: "+0.3 damage. Adds a chance to fire acidic tears which can destroy obstacles and open doors."),
    Item(id: 464, name: "Glyph of Balance", imagePath: "assets/images/Glyph of Balance.png", desc: "A gift from above", effect: "Grants 2 Soul Hearts and modifies drops from Champion monsters based on certain conditions. Also modifies room clear drops."),
    Item(id: 465, name: "Analog Stick", imagePath: "assets/images/Analog Stick.png", desc: "360 tears!", effect: "+0.35 tears up. Allows Isaac to fire tears diagonally."),
    Item(id: 466, name: "Contagion", imagePath: "assets/images/Contagion.png", desc: "Outbreak!", effect: "The first enemy killed in a room releases a gas cloud that poisons nearby enemies. Affected enemies will also release gas upon death, spreading the effect."),
    Item(id: 467, name: "Finger!", imagePath: "assets/images/Finger!.png", desc: "Watch where you point that!", effect: "Spawns a finger familiar that faces in the same direction as Isaac, rapidly damaging enemies it points at for 10% of Isaac's damage."),
    Item(id: 468, name: "Shade", imagePath: "assets/images/Shade.png", desc: "It follows", effect: "Spawns a shadow familiar that follows Isaac's movements on a 1 second delay and deals contact damage to enemies. After dealing enough damage, it is absorbed by Isaac and spawns friendly Chargers."),
    Item(id: 469, name: "Depression", imagePath: "assets/images/Depression.png", desc: ":(", effect: "Spawns a familiar that leaves a trail of creep that damages enemies at 6 damage per second. Has a chance to spawn damaging beams of light onto enemies that touch the cloud."),
    Item(id: 470, name: "Hushy", imagePath: "assets/images/Hushy.png", desc: "Lil hush!", effect: "Spawns a Hush familiar that bounces diagonally around the room and deals contact damage. While firing tears, it stops moving."),
    Item(id: 471, name: "Lil Monstro", imagePath: "assets/images/Lil Monstro.png", desc: "Ain't he cute?", effect: "Spawns a familiar that fires clusters of tears. Must be charged before shooting."),
    Item(id: 472, name: "King Baby", imagePath: "assets/images/King Baby.png", desc: "Hail to the king baby", effect: "Spawns a familiar that follows Isaac and makes other familiars follow it. While firing tears, other familiars stop moving."),
    Item(id: 473, name: "Big Chubby", imagePath: "assets/images/Big Chubby.png", desc: "Chub chub", effect: "Spawns a familiar that follows Isaac and charges across the room when Isaac fires, blocking shots and dealing 40.5 damage per second on contact."),
    Item(id: 474, name: "Broken Glass Canon", imagePath: "assets/images/Broken Glass Canon.png", desc: "You broke it!", effect: "Begins with no charges and turns into Glass Cannon when used. Only acquired by taking damage while holding Glass Cannon."),
    Item(id: 475, name: "Plan C", imagePath: "assets/images/Plan C.png", desc: "My last resort", effect: "Kills all enemies in the room, then kills Isaac three seconds later."),
    Item(id: 476, name: "D1", imagePath: "assets/images/D1.png", desc: "What will it be?", effect: "Duplicates a random pickup in the room. (in Repentance) The new pickup may be different than the original (i.e. a different card, etc)."),
    Item(id: 477, name: "Void", imagePath: "assets/images/Void.png", desc: "Consume", effect: "Consumes all pedestal items in the room. Active items will have their effects added to Void, and passive items will grant random stat upgrades."),
    Item(id: 478, name: "Pause", imagePath: "assets/images/Pause.png", desc: "Stop!", effect: "Freezes all enemies until a fire button is pressed."),
    Item(id: 479, name: "Smelter", imagePath: "assets/images/Smelter.png", desc: "Trinket melter!", effect: "Consumes Isaac's currently held trinket and applies its effect permanently."),
    Item(id: 480, name: "Compost", imagePath: "assets/images/Compost.png", desc: "Gain more friends!", effect: "Doubles Isaac's Blue Flies and Blue Spiders, or spawns one if Isaac has none. Converts pickups in the current room into Blue Flies or Blue Spiders."),
    Item(id: 481, name: "Dataminer", imagePath: "assets/images/Dataminer.png", desc: "109", effect: "Displaces enemy sprites for the current room, randomly increases one stat and decreases another."),
    Item(id: 482, name: "Clicker", imagePath: "assets/images/Clicker.png", desc: "Change", effect: "Transforms Isaac into a random character and removes the last item collected."),
    Item(id: 483, name: "Mama Mega!", imagePath: "assets/images/Mama Mega!.png", desc: "BOOOOOOOOOM!", effect: "Damages all enemies and destroys all obstacles. The effect occurs every time a new room is entered for the rest of the floor. Opens the doors to the floor's Secret Rooms and Super Secret Rooms."),
    Item(id: 484, name: "Wait What", imagePath: "assets/images/Wait What.png", desc: "I can't believe it's not butter bean!", effect: "Isaac farts, sending out a circular rock wave that destroys obstacles and hurts enemies."),
    Item(id: 485, name: "Crooked Penny", imagePath: "assets/images/Crooked Penny.png", desc: "50/50", effect: "Spawns a penny and has a 50% chance to double all items and pickups in the room. If the effect fails, the items and pickups disappear."),
    Item(id: 486, name: "Dull Razor", imagePath: "assets/images/Dull Razor.png", desc: "I feel numb...", effect: "Hurts Isaac without depleting health, activating any items that trigger upon taking damage."),
    Item(id: 487, name: "Potato Peeler", imagePath: "assets/images/Potato Peeler.png", desc: "A pound of flesh", effect: "Removes a Heart Container, increases damage and range, and grants a Cube of Meat."),
    Item(id: 488, name: "Metronome", imagePath: "assets/images/Metronome.png", desc: "Waggles a finger", effect: "Grants a random item effect for the current room."),
    Item(id: 489, name: "D Infinity", imagePath: "assets/images/D Infinity.png", desc: "Reroll forever", effect: "Acts as any die item, and can by cycled by pressing the drop key."),
    Item(id: 490, name: "Eden's Soul", imagePath: "assets/images/Eden's Soul.png", desc: "...", effect: "Spawns two item pedestals then is consumed. Begins with no charges."),
    Item(id: 491, name: "Acid Baby", imagePath: "assets/images/Acid Baby.png", desc: "Pills pills pills!", effect: "Spawns a familiar that drops a random pill every 3 rooms. Upon using a pill, all enemies in the room are poisoned."),
    Item(id: 492, name: "YO LISTEN!", imagePath: "assets/images/YO LISTEN!.png", desc: "Yo listen!", effect: "+1 luck. Spawns a fairy familiar that points out Secret Rooms, Crawl Space entrances, and tinted rocks. "),
    Item(id: 493, name: "Adrenaline", imagePath: "assets/images/Adrenaline.png", desc: "Panic = power", effect: "Increases damage based on how many empty Red Heart containers Isaac has."),
    Item(id: 494, name: "Jacob's Ladder", imagePath: "assets/images/Jacob's Ladder.png", desc: "Electric tears", effect: "Tears fire sparks of electricity in random directions upon hitting an obstacle or enemy."),
    Item(id: 495, name: "Ghost Pepper", imagePath: "assets/images/Ghost Pepper.png", desc: "Flame tears", effect: "Gives a chance to shoot blue fires alongside tears that become smaller as they travel."),
    Item(id: 496, name: "Euthanasia", imagePath: "assets/images/Euthanasia.png", desc: "Needle shot", effect: "Adds a luck-based chance to fire needles that instantly kill basic enemies and deal triple damage to bosses. If a needle kills an enemy, it bursts into more tears/needles."),
    Item(id: 497, name: "Camo Undies", imagePath: "assets/images/Camo Undies.png", desc: "Camo kid", effect: "Isaac becomes camouflaged at the beginning of each room. Until he shoots, all non-boss enemies will be confused. Isaac gains +0.5 Speed while camouflaged, and gains Tears and Damage upon breaking it."),
    Item(id: 498, name: "Duality", imagePath: "assets/images/Duality.png", desc: "You feel very balanced", effect: "Whenever a Devil Room or Angel Room would spawn after defeating a boss, both rooms spawn. Only one can be entered."),
    Item(id: 499, name: "Eucharist", imagePath: "assets/images/Eucharist.png", desc: "Peace be with you", effect: "An Angel Room will always spawn after every boss fight, excluding the first floor and floors after Chapter 4."),
    Item(id: 500, name: "Sack of Sacks", imagePath: "assets/images/Sack of Sacks.png", desc: "Gives sacks", effect: "Spawns a familiar that drops a Grab Bag every 5-6 rooms cleared."),
    Item(id: 501, name: "Greed's Gullet", imagePath: "assets/images/Greed's Gullet.png", desc: "Money = health!", effect: "Grants a Red Heart container for every 25 Coins Isaac has."),
    Item(id: 502, name: "Large Zit", imagePath: "assets/images/Large Zit.png", desc: "Creep shots", effect: "Adds a chance to fire a creep shot that deals double damage and leaves a trail of slowing white creep."),
    Item(id: 503, name: "Little Horn", imagePath: "assets/images/Little Horn.png", desc: "Big brother is watching", effect: "Adds a chance to fire a tear that summons Big Horn's hand to crush the first enemy it hits, instantly killing monsters or greatly damaging bosses."),
    Item(id: 504, name: "Brown Nugget", imagePath: "assets/images/Brown Nugget.png", desc: "Friendly fly", effect: "Spawns a stationary fly turret that lasts for the current room and fires tears at nearby enemies."),
    Item(id: 505, name: "Poke Go", imagePath: "assets/images/Poke Go.png", desc: "Gotta catch em...", effect: "Upon entering a room with enemies in it, has a chance to spawn a random friendly enemy that persists across rooms."),
    Item(id: 506, name: "Backstabber", imagePath: "assets/images/Backstabber.png", desc: "Watch your back!", effect: "Isaac's tears deal double damage and inflict bleeding on enemies if they're facing away from Isaac."),
    Item(id: 507, name: "Sharp Straw", imagePath: "assets/images/Sharp Straw.png", desc: "More blood!", effect: "Damages all enemies in the room. Has a chance to drop a Half Red Heart if an enemy is hit."),
    Item(id: 508, name: "Mom's Razor", imagePath: "assets/images/Mom's Razor.png", desc: "It's sharp!", effect: "Spawns an orbiting razor that deals contact damage and inflicts bleeding on enemies, causing them to slowly take damage over time."),
    Item(id: 509, name: "Bloodshot Eye", imagePath: "assets/images/Bloodshot Eye.png", desc: "Bloody friend", effect: "Spawns an orbital familiar that deals contact damage, blocks shots, and fires tears at enemies in its line of sight."),
    Item(id: 510, name: "Delirious", imagePath: "assets/images/Delirious.png", desc: "Unleash the power!", effect: "Delirium as a random friendly boss that fights for Isaac."),
    Item(id: 511, name: "Angry Fly", imagePath: "assets/images/Angry Fly.png", desc: "He's violent", effect: "Spawns a fly familiar that orbits around a random enemy and deals 30 damage per second to enemies it comes in contact with."),
    Item(id: 512, name: "Black Hole", imagePath: "assets/images/Black Hole.png", desc: "Nothing can escape", effect: "Throws a black hole on the ground which sucks in and damages enemies and destroys nearby obstacles."),
    Item(id: 513, name: "Bozo", imagePath: "assets/images/Bozo.png", desc: "Party time!", effect: "+0.1 flat damage, grants a Soul Heart. Enemies can randomly become briefly charmed or feared. Taking damage has a chance to spawn a Rainbow Poop."),
    Item(id: 514, name: "Broken Modem", imagePath: "assets/images/Broken Modem.png", desc: "Lag!", effect: "Enemies, enemy shots, tears, and pickups will briefly freeze in place at random intervals. The reward from clearing a room will sometimes be duplicated."),
    Item(id: 515, name: "Mystery Gift", imagePath: "assets/images/Mystery Gift.png", desc: "Wrapped up nice for you!", effect: "Spawns a random item from the current room's item pool, with a chance to spawn A Lump of Coal or The Poop instead."),
    Item(id: 516, name: "Sprinkler", imagePath: "assets/images/Sprinkler.png", desc: "Sprinkles.", effect: "Creates a stationary sprinkler for the current room that rotates and fires tears in 8 directions. The tears copy Isaac's stats and most tear effects."),
    Item(id: 517, name: "Fast Bombs", imagePath: "assets/images/Fast Bombs.png", desc: "Rapid bomb drops", effect: "+7 Bombs. Removes the delay between bomb placements, allowing them to be placed rapidly. Bombs will no longer knock each other away."),
    Item(id: 518, name: "Buddy in a Box", imagePath: "assets/images/Buddy in a Box.png", desc: "What could it be?!", effect: "Spawns a familiar with a random tear effect. The familiar is randomized at the start of each floor."),
    Item(id: 519, name: "Lil Delirium", imagePath: "assets/images/Lil Delirium.png", desc: "Delirious friend", effect: "Grants a familiar that transforms into another random familiar every 10 seconds."),
    Item(id: 520, name: "Jumper Cables", imagePath: "assets/images/Jumper Cables.png", desc: "Bloody recharge", effect: "Grants an active item charge every 15 enemies killed."),
    Item(id: 521, name: "Coupon", imagePath: "assets/images/Coupon.png", desc: "Allow 6 weeks for delivery", effect: "While held, guarantees one item in every Shop will be on sale. On use, makes a random item in the current room free."),
    Item(id: 522, name: "Telekinesis", imagePath: "assets/images/Telekinesis.png", desc: "Power of the mind", effect: "Any enemy shots that come near Isaac for the next three seconds are held in place then fired back."),
    Item(id: 523, name: "Moving Box", imagePath: "assets/images/Moving Box.png", desc: "Pack and unpack", effect: "If the box is empty, picks up items and pickups in a room and stores them. If the box is not empty, places all stored items and pickups in the current room. Can carry up to 10 items or pickups at once."),
    Item(id: 524, name: "Technology Zero", imagePath: "assets/images/Technology Zero.png", desc: "Static tears!", effect: "Connects Isaac's tears with electricity that can damage enemies."),
    Item(id: 525, name: "Leprosy", imagePath: "assets/images/Leprosy.png", desc: "You're tearing me apart!", effect: "Taking damage grants a body part orbital that blocks shots and deals contact damage, up to a maximum of three. The body parts are destroyed after blocking enough damage."),
    Item(id: 526, name: "7 Seals", imagePath: "assets/images/7 Seals.png", desc: "Lil harbingers!", effect: "Spawns a random Harbinger familiar that changes every 10 seconds. Harbingers have different behaviors and spawn themed Locusts."),
    Item(id: 527, name: "Mr. ME!", imagePath: "assets/images/Mr. ME!.png", desc: "Caaan do!", effect: "Summons a controllable ghost that can perform various tasks depending on what it targets."),
    Item(id: 528, name: "Angelic Prism", imagePath: "assets/images/Angelic Prism.png", desc: "Eclipsed by the moon", effect: "Spawns an orbital familiar with a large orbital radius. Tears that pass through the prism split into four multi-colored tears."),
    Item(id: 529, name: "Pop!", imagePath: "assets/images/Pop!.png", desc: "Eyeball tears!", effect: "Replaces Isaac's tears with eyeballs that can bounce off of each other. The eyeballs are not limited by range, and will instead dissipate when they stop moving or when hitting an enemy or obstacle."),
    Item(id: 530, name: "Death's List", imagePath: "assets/images/Death's List.png", desc: "Just hope you're not next...", effect: "Grants a pickup or stat upgrade if all enemies in a room are killed in the order they are marked."),
    Item(id: 531, name: "Haemolacria", imagePath: "assets/images/Haemolacria.png", desc: "I'm seeing red...", effect: "Tears are now fired in an arc. Upon hitting the floor, an obstacle, or an enemy, they burst into many smaller tears in all directions. Increases tear delay."),
    Item(id: 532, name: "Lachryphagy", imagePath: "assets/images/Lachryphagy.png", desc: "Feed them!", effect: "Isaac's tears decelerate as they travel. Upon stopping or hitting an obstacle, they burst into 8 tears in all directions. Tears can be fired into other tears to feed them, increasing their damage."),
    Item(id: 533, name: "Trisagion", imagePath: "assets/images/Trisagion.png", desc: "Smite thy enemy", effect: "Tears are replaced with piercing white blasts of light that deal less damage but can damage the same enemy multiple times."),
    Item(id: 534, name: "Schoolbag", imagePath: "assets/images/Schoolbag.png", desc: "Extra active item room", effect: "Allows Isaac to hold two active items."),
    Item(id: 535, name: "Blanket", imagePath: "assets/images/Blanket.png", desc: "You feel Safe", effect: "Grants one Soul Heart. Upon entering a boss room, grants a shield that blocks one hit, then disappears."),
    Item(id: 536, name: "Sacrificial Altar", imagePath: "assets/images/Sacrificial Altar.png", desc: "He demands a sacrifice", effect: "Sacrifices up to 2 familiars and spawns an item for each familiar sacrificed. Converts all friendly spiders and flies into coins"),
    Item(id: 537, name: "Lil Spewer", imagePath: "assets/images/Lil Spewer.png", desc: "Puking buddy", effect: "Drops a random pill, and spawns a familiar that charges up and fires creep. Using pills changes the creep type."),
    Item(id: 538, name: "Marbles", imagePath: "assets/images/Marbles.png", desc: "Choking hazard", effect: "Spawns 3 random trinkets. Taking damage has a 5% chance to swallow Isaac's currently held trinket, gaining its effect permanently."),
    Item(id: 539, name: "Mystery Egg", imagePath: "assets/images/Mystery Egg.png", desc: "Sacrificial insemination", effect: "Spawns an egg familiar, which hatches a random friendly monster when Isaac is hurt once per room. The power of the monster improves for each consecutive room cleared without taking damage."),
    Item(id: 540, name: "Flat Stone", imagePath: "assets/images/Flat Stone.png", desc: "Skipping tears", effect: "Tears bounce off the floor, creating splashes. Splashes deal 25% of Isaac's tear damage to all enemies in a small area."),
    Item(id: 541, name: "Marrow", imagePath: "assets/images/Marrow.png", desc: "HP up?", effect: "Grants one Bone Heart and spawns three Red Hearts."),
    Item(id: 542, name: "Slipped Rib", imagePath: "assets/images/Slipped Rib.png", desc: "Projectile shield", effect: "Spawns a bone familiar that circles around Isaac and reflects enemy shots."),
    Item(id: 543, name: "Hallowed Ground", imagePath: "assets/images/Hallowed Ground.png", desc: "Portable sanctuary", effect: "Spawns a white poop familiar, which turns into a White Poop when Isaac is hurt. White poop has an aura that increases tear fire rate and can block enemy shots. The aura also increases damage by 20% and gives homing."),
    Item(id: 544, name: "Pointy Rib", imagePath: "assets/images/Pointy Rib.png", desc: "Stabbing time", effect: "Spawns a bone familiar that hovers back and forth in front of Isaac, dealing damage equal to Isaac's damage."),
    Item(id: 545, name: "Book of the Dead", imagePath: "assets/images/Book of the Dead.png", desc: "Rise from the grave", effect: "Creates a friendly Bony, Black Bony, Bone Fly, Revenant, or an orbital bone for each enemy killed in the current room."),
    Item(id: 546, name: "Dad's Ring", imagePath: "assets/images/Dad's Ring.png", desc: "Father's blessing", effect: "Creates a ring of light around Isaac that petrifies enemies in contact."),
    Item(id: 547, name: "Divorce Papers", imagePath: "assets/images/Divorce Papers.png", desc: "Tears up + you feel empty", effect: "Grants one Bone Heart, +0.7 tears, spawns a Mysterious Paper."),
    Item(id: 548, name: "Jaw Bone", imagePath: "assets/images/Jaw Bone.png", desc: "Fetch!", effect: "Spawns a familiar that flies through the room while firing, dealing a flat 7 contact damage to enemies and retrieving pickups."),
    Item(id: 549, name: "Brittle Bones", imagePath: "assets/images/Brittle Bones.png", desc: "Everything hurts", effect: "Replaces all Red Heart Containers with 6 empty Bone Hearts. Upon losing a Bone Heart, Isaac fires bones in 8 directions and permanently gains a tears up. Upon losing a Bone Heart, Isaac fires bones in 8 directions and permanently gains a fire rate up."),
    Item(id: 550, name: "Broken Shovel 1 (handle)", imagePath: "assets/images/Broken Shovel 1 (handle).png", desc: "It feels cursed", effect: "While held, causes Mom to continuously attempt to stomp on Isaac. Activating makes her stop for the current room or wave. One of two parts necessary to create Mom's Shovel."),
    Item(id: 551, name: "Broken Shovel 2 (head)", imagePath: "assets/images/Broken Shovel 2 (head).png", desc: "It feels cursed", effect: "One of two parts necessary to create Mom's Shovel. No effect if somehow obtained on its own."),
    Item(id: 552, name: "Mom's Shovel", imagePath: "assets/images/Mom's Shovel.png", desc: "Lost but not forgotten", effect: "Creates a trapdoor that leads to the next floor. Unlocks The Forgotten when used on a mound of dirt that appears in Dark Room."),
    Item(id: 553, name: "Mucormycosis", imagePath: "assets/images/Mucormycosis.png", desc: "Spore shot", effect: "Gives Isaac the chance to fire spore tears that stick to enemies and blow up after a few seconds, dealing damage and poisoning nearby enemies and releasing more spores."),
    Item(id: 554, name: "2Spooky", imagePath: "assets/images/2Spooky.png", desc: "4me", effect: "Enemies near Isaac are afflicted with fear."),
    Item(id: 555, name: "Golden Razor", imagePath: "assets/images/Golden Razor.png", desc: "Pain from gain", effect: "Spends five Pennies to increase Isaac's damage by 1.2 for the current room."),
    Item(id: 556, name: "Sulfer", imagePath: "assets/images/Sulfer.png", desc: "Temporary demon form", effect: "Grants Brimstone for the current room."),
    Item(id: 557, name: "Fortune Cookie", imagePath: "assets/images/Fortune Cookie.png", desc: "Reusable fortunes", effect: "Gives Isaac a fortune, Soul Heart, Card, Rune, or trinket, similar to a Fortune Telling Machine. Fortunes are much more likely to be given than anything else."),
    Item(id: 558, name: "Eye Sore", imagePath: "assets/images/Eye Sore.png", desc: "More eyes", effect: "Causes Isaac to occasionally fire extra tears in random directions while shooting."),
    Item(id: 559, name: "120 Volt", imagePath: "assets/images/120 Volt.png", desc: "Zap!", effect: "Repeatedly zaps enemies that are close to Isaac."),
    Item(id: 560, name: "It Hurts", imagePath: "assets/images/It Hurts.png", desc: "No it doesn't...", effect: "Releases a ring of tears and increases fire rate for the current room when Isaac takes damage."),
    Item(id: 561, name: "Almond Milk", imagePath: "assets/images/Almond Milk.png", desc: "DMG down + tears up + you feel nutty", effect: "Quadruples fire rate, with Ã—0.3 damage multiplier. Each tear is given a random effect that alters its movement."),
    Item(id: 562, name: "Rock Bottom", imagePath: "assets/images/Rock Bottom.png", desc: "It's only up from there", effect: "Locks Isaac's attributes to their highest values reached."),
    Item(id: 563, name: "Nancy Bombs", imagePath: "assets/images/Nancy Bombs.png", desc: "Random blast +5 bombs", effect: "+5 Bombs. Each bomb gains 1-4 random bomb synergies."),
    Item(id: 564, name: "A Bar of Soap", imagePath: "assets/images/A Bar of Soap.png", desc: "Tears + shot speed up", effect: "+0.5 Tears, +0.2 Shot Speed."),
    Item(id: 565, name: "Blood Puppy", imagePath: "assets/images/Blood Puppy.png", desc: "What a cute little thing!", effect: "Spawns a familiar that chases enemies. After killing enough enemies, it becomes more powerful but will also try to hurt Isaac, it will also generate Half Red Hearts on enemy kills. It can increase in power one more stage after killing even more enemies. Dealing enough damage to it returns it to normal."),
    Item(id: 566, name: "Dream Catcher", imagePath: "assets/images/Dream Catcher.png", desc: "Sweet dreams", effect: "During the transition between floors, Isaac's nightmare shows the contents of the upcoming floor's Treasure Room, as well as the floor's boss. Grants a Half Soul Heart when entering a new floor."),
    Item(id: 567, name: "Paschal Candle", imagePath: "assets/images/Paschal Candle.png", desc: "Keep the flame burning", effect: "Gives Isaac a familiar that grants +0.4 Fire Rate for each consecutive room he's cleared without taking damage. The bonus stacks up to five times."),
    Item(id: 568, name: "Divine Intervention", imagePath: "assets/images/Divine Intervention.png", desc: "Double tap shield", effect: "Double tapping a fire key creates a shield that pushes away enemies and reflects enemy projectiles and lasers."),
    Item(id: 569, name: "Blood Oath", imagePath: "assets/images/Blood Oath.png", desc: "Bleed me dry", effect: "Stabs Isaac at the beginning of each floor, draining his Red Hearts. Increases damage and speed for the floor based on the amount drained."),
    Item(id: 570, name: "Playdough Cookie", imagePath: "assets/images/Playdough Cookie.png", desc: "Tasty rainbow", effect: "Grants multicolored tears. Each color comes with a different status or tear effect."),
    Item(id: 571, name: "Orphan Socks", imagePath: "assets/images/Orphan Socks.png", desc: "Speed up + your feet feel stronger", effect: "Prevents creep and floor spikes from damaging Isaac. Also gives Isaac +0.3 speed and 2 Soul Hearts."),
    Item(id: 572, name: "Eye of the Occult", imagePath: "assets/images/Eye of the Occult.png", desc: "DMG up + range up + controlled tears", effect: "+1 damage, +2 range, -0.16 shot speed. Isaac's tears can be controlled mid-flight with tear controls."),
    Item(id: 573, name: "Immaculate Heart", imagePath: "assets/images/Immaculate Heart.png", desc: "Halo of tears", effect: "Adds one Red Heart Container and fully restores health. x1.2 damage multiplier. Isaac will occasionally fire extra tears that orbit around him."),
    Item(id: 574, name: "Monstrance", imagePath: "assets/images/Monstrance.png", desc: "Purifying light", effect: "Isaac gains an aura that deals damage to enemies inside it."),
    Item(id: 575, name: "The Intruder", imagePath: "assets/images/The Intruder.png", desc: "Invasive friend", effect: "A familiar burrows in Isaac's head, firing 4 slowing tears. Upon taking damage, there's a chance it will burst out of Isaac's head, spawning blue spiders and chasing enemies for the current room."),
    Item(id: 576, name: "Dirty Mind", imagePath: "assets/images/Dirty Mind.png", desc: "Filthy friends", effect: "Destroying poop spawns Dip Familiars that follow Isaac and damage enemies. Destroying different types of poop spawns different Dips with special effects."),
    Item(id: 577, name: "Damocles", imagePath: "assets/images/Damocles.png", desc: "A king's fortune... but at what cost?", effect: "Spawns a sword above Isaac's head, which doubles all item pedestals. After Isaac takes damage, the sword can randomly fall at any point, instantly killing him."),
    Item(id: 578, name: "Free Lemonade", imagePath: "assets/images/Free Lemonade.png", desc: "Party time!", effect: "Creates a large pool of yellow creep beneath Isaac."),
    Item(id: 579, name: "Spirit Sword", imagePath: "assets/images/Spirit Sword.png", desc: "Divine blade", effect: "Instead of tears, Isaac swings a sword. Charging does a spin attack and fires a projectile, and a projectile is sometimes fired with normal swings if Isaac has no empty Red Heart Containers."),
    Item(id: 580, name: "Red Key", imagePath: "assets/images/Red Key.png", desc: "Explore the other side", effect: "Creates a Red Room adjacent to a viable wall, indicated by the outline of a door. These rooms have a 10% chance to be a special room."),
    Item(id: 581, name: "Psy Fly", imagePath: "assets/images/Psy Fly.png", desc: "Flamboyant protector", effect: "Spawns a fly orbital familiar that seeks out incoming enemy shots and destroys them."),
    Item(id: 582, name: "Wavy Cap", imagePath: "assets/images/Wavy Cap.png", desc: "Tears up. A mind changing experience!", effect: "Increases fire rate, decreases speed, and distorts the screen. All effects become more pronounced with repeated use, and wear off by clearing rooms."),
    Item(id: 583, name: "Rocket in a Jar", imagePath: "assets/images/Rocket in a Jar.png", desc: "Rocket propulsion +5 bombs", effect: "+5 Bombs. If Isaac is currently firing, placing a bomb will instead shoot a fast-moving, explosive rocket in the direction he's facing."),
    Item(id: 584, name: "Book of Virtues", imagePath: "assets/images/Book of Virtues.png", desc: "Spiritual companionship", effect: "Spawns a destroyable orbiting wisp familiar that fires tears and deals contact damage. Also acts as a passive item that causes other active items to spawn themed wisps."),
    Item(id: 585, name: "Alabaster Box", imagePath: "assets/images/Alabaster Box.png", desc: "A sacred offering", effect: "Spawns three Soul Hearts and two Angel Room items and is consumed. Starts with no charges, and can only be charged by picking up 6 Soul Hearts."),
    Item(id: 586, name: "The Stairway", imagePath: "assets/images/The Stairway.png", desc: "May you get what you came for", effect: "Generates a ladder at the start of every floor that leads to a unique Angel Room shop."),

    Item(id: 588, name: "Sol", imagePath: "assets/images/Sol.png", desc: "Radiant victory", effect: "Reveals the location of the Boss Room. Upon defeating a boss, activates XIX - The Sun, fully recharges Isaac's active item, grants bonus damage and luck for the rest of the floor, and removes curses."),
    Item(id: 589, name: "Luna", imagePath: "assets/images/Luna.png", desc: "The moon's blessing shines upon you", effect: "Adds an extra Secret Room and Super Secret Room to each floor. Secret Rooms contain a beam of light that increases Fire Rate for the current floor and gives a Half Soul Heart."),
    Item(id: 590, name: "Mercurius", imagePath: "assets/images/Mercurius.png", desc: "Speed up + you feel elusive", effect: "+0.4 speed. Doors stay open after entering a room, even in rooms with enemies."),
    Item(id: 591, name: "Venus", imagePath: "assets/images/Venus.png", desc: "HP up + you feel pretty", effect: "Adds one red Heart container. Heals 1 additional Red Heart. Enemies near Isaac become charmed."),
    Item(id: 592, name: "Terra", imagePath: "assets/images/Terra.png", desc: "Born to rock", effect: "+1 Damage. Replaces tears with rocks that deal variable damage, have increased knockback, and can destroy obstacles."),
    Item(id: 593, name: "Mars", imagePath: "assets/images/Mars.png", desc: "Double tap dash", effect: "Dash by double-tapping the movement key, becoming invulnerable and damaging enemies. There's a short cooldown before Isaac can dash again."),
    Item(id: 594, name: "Jupiter", imagePath: "assets/images/Jupiter.png", desc: "You're a gas giant!", effect: "+2 red Heart containers, -0.3 speed. Isaac builds speed while standing still, and releases poison gas clouds when he moves afterwards."),
    Item(id: 595, name: "Saturnus", imagePath: "assets/images/Saturnus.png", desc: "Ring of tears", effect: "Upon entering a room, a circle of tears orbits Isaac. Enemy tears have a chance to be caught in orbit, turning them friendly."),
    Item(id: 596, name: "Uranus", imagePath: "assets/images/Uranus.png", desc: "Ice tears", effect: "Isaac shoots ice tears, which slow enemies and freeze monsters they kill. Frozen enemies slide away and explode into ice shards when touched."),
    Item(id: 597, name: "Neptunus", imagePath: "assets/images/Neptunus.png", desc: "Open the floodgates", effect: "Isaac's mouth fills with tears over time while not firing, up to a cap. Holding the fire button will release all stored tears in rapid succession."),
    Item(id: 598, name: "Pluto", imagePath: "assets/images/Pluto.png", desc: "Size down", effect: "+0.7 tears. Significantly shrinks Isaac, allowing him to squeeze between objects and makes some enemy shots pass over him."),
    Item(id: 599, name: "Voodoo Head", imagePath: "assets/images/Voodoo Head.png", desc: "Extra curse rooms", effect: "Spawns an additional Curse Room on each floor. Curse Rooms have improved layouts."),
    Item(id: 600, name: "Eye Drops", imagePath: "assets/images/Eye Drops.png", desc: "Tears up", effect: "Increases the fire rate in Isaac's left eye, causing his eyes to fire at different rates."),
    Item(id: 601, name: "Act of Contrition", imagePath: "assets/images/Act of Contrition.png", desc: "Tears up, you feel forgiven", effect: "+0.7 tears, grants an Eternal Heart. Allows Angel Rooms to appear even if a Devil Deal has been taken before. Reduces the Devil/Angel room chance penalty for taking Red heart damage."),
    Item(id: 602, name: "Member Card", imagePath: "assets/images/Member Card.png", desc: "Exclusive access!", effect: "Adds a trapdoor to every Shop that leads to a second shop with a unique stock and marked-up prices."),
    Item(id: 603, name: "Battery Pack", imagePath: "assets/images/Battery Pack.png", desc: "Instant energy!", effect: "Recharges Isaac's currently held active item and drops 2-4 batteries."),
    Item(id: 604, name: "Mom's Bracelet", imagePath: "assets/images/Mom's Bracelet.png", desc: "Mother's strength", effect: "Lets Isaac pick up and throw rocks, TNT, Poops, friendly Dips, Bombs, and other obstacles."),
    Item(id: 605, name: "The Scooper", imagePath: "assets/images/The Scooper.png", desc: "Plop!", effect: "Summons The Peeper for the current room, which leaves a trail of red creep."),
    Item(id: 606, name: "Ocular Rift", imagePath: "assets/images/Ocular Rift.png", desc: "Stare into the abyss", effect: "Adds a chance to fire tears that create rifts where they land, which suck enemies, pickups, and shots towards them and damage enemies in the area."),
    Item(id: 607, name: "Boiled Baby", imagePath: "assets/images/Boiled Baby.png", desc: "Messy friend", effect: "Gives Isaac a familiar that fires chaotic bursts of tears in all directions."),
    Item(id: 608, name: "Freezer Baby", imagePath: "assets/images/Freezer Baby.png", desc: "Iced iced baby", effect: "Spawns a shooting familiar that has a chance to petrify enemies, and freezes monsters upon killing them."),
    Item(id: 609, name: "Eternal D6", imagePath: "assets/images/Eternal D6.png", desc: "???", effect: "Rerolls all items in the room. Each item has a 25% chance to disappear instead."),
    Item(id: 610, name: "Bird Cage", imagePath: "assets/images/Bird Cage.png", desc: "Fat buddy", effect: "The first time Isaac takes damage in a room, a familiar leaps on an enemy, dealing damage and releasing a rock wave. For the rest of the room, it chases enemies similar to Dead Bird."),
    Item(id: 611, name: "Larynx", imagePath: "assets/images/Larynx.png", desc: "Hear my pain", effect: "Isaac shouts, damaging and pushing away nearby enemies. The shout gets stronger the more charges the item has. Larynx gains 1 charge whenever Isaac takes damage, in addition to charging normally."),
    Item(id: 612, name: "Lost Soul", imagePath: "assets/images/Lost Soul.png", desc: "Protect him", effect: "Creates a ghostly familiar that closely follows behind Isaac. It dies in one hit, but respawns at the start of the next floor. Each time it survives until the next floor, it rewards Isaac with Soul Hearts, Eternal Hearts, or an Item."),

    Item(id: 614, name: "Blood Bombs", imagePath: "assets/images/Blood Bombs.png", desc: "Bloody blast + HP up", effect: "Grants a Red Heart container and restores 5 hearts. Bombs leave behind damaging red creep. Isaac can place Bombs at the cost of half a heart if he doesn't have any."),
    Item(id: 615, name: "Lil Dumpy", imagePath: "assets/images/Lil Dumpy.png", desc: "Puffy buddy", effect: "Spawns a Dumpy familiar that pushes away nearby enemies and projectiles just before Isaac is about to be hit, then falls asleep. He can be awoken by walking to him."),
    Item(id: 616, name: "Bird's Eye", imagePath: "assets/images/Bird's Eye.png", desc: "It burns", effect: "Gives a chance to shoot fires alongside tears that block enemy shots and deal damage to enemies that touch them."),
    Item(id: 617, name: "Lodestone", imagePath: "assets/images/Lodestone.png", desc: "Magnetizing tears", effect: "Gives Isaac the chance to fire tears that magnetize enemies they hit, causing them to attract nearby pickups and enemies, as well as tears/shots from all sources."),
    Item(id: 618, name: "Rotten Tomato", imagePath: "assets/images/Rotten Tomato.png", desc: "Delicious!", effect: "Adds a chance to fire tears that mark enemies with the Bait status effect. Enemies marked as bait are attacked by other enemies."),
    Item(id: 619, name: "Birthright", imagePath: "assets/images/Birthright.png", desc: "???", effect: "Grants a character-specific effect that enhances their special abilities."),

    Item(id: 621, name: "Red Stew", imagePath: "assets/images/Red Stew.png", desc: "Full HP + temporary DMG up", effect: "Refills all heart containers. Grants an enormous damage boost that wears off over time."),
    Item(id: 622, name: "Genesis", imagePath: "assets/images/Genesis.png", desc: "In the beginning", effect: "Removes all of Isaac's items and pickups and takes him to a unique Bedroom with some pickups and Chests and a trapdoor to the next floor. For every item Isaac lost, he gets to take an item from a set of three."),
    Item(id: 623, name: "Sharp Key", imagePath: "assets/images/Sharp Key.png", desc: "Open your enemies", effect: "Adds 5 Keys on pickup. Allows Isaac to throw his keys to deal damage to enemies, destroy obstacles, and open doors."),
    Item(id: 624, name: "Booster Pack", imagePath: "assets/images/Booster Pack.png", desc: "Collect them all!", effect: "Drops 5 random cards."),
    Item(id: 625, name: "Mega Mush", imagePath: "assets/images/Mega Mush.png", desc: "I'm a big boy now!", effect: "Gigantifies Isaac for 30 seconds, increasing damage and range while lowering tears, granting invulnerability, and allowing Isaac to crush enemies and obstacles he walks on. The effect persists between rooms."),
    Item(id: 626, name: "Knife Piece 1 (handle)", imagePath: "assets/images/Knife Piece 1 (handle).png", desc: "???", effect: "Combined with Knife Piece 2, creates a knife familiar that damages enemies and can open the door to the Corpse."),
    Item(id: 627, name: "Knife Piece 2 (blade)", imagePath: "assets/images/Knife Piece 2 (blade).png", desc: "???", effect: "Combined with Knife Piece 1, creates a knife familiar that damages enemies and can open the door to the Corpse."),
    Item(id: 628, name: "Death Certificate", imagePath: "assets/images/Death Certificate.png", desc: "Where am I?", effect: "Takes Isaac to an abandoned and expanded version of Home, which has one copy of every item in the game. After taking an item, Isaac returns to the room he used Death Certificate in."),
    Item(id: 629, name: "Bot Fly", imagePath: "assets/images/Bot Fly.png", desc: "Defense drone", effect: "Spawns an orbiting fly familiar that fires shielded tears at incoming enemy projectiles."),

    Item(id: 631, name: "Meat Cleaver", imagePath: "assets/images/Meat Cleaver.png", desc: "Slice but no dice", effect: "Splits all enemies in the room into two smaller versions of themselves with much less health."),
    Item(id: 632, name: "Evil Charm", imagePath: "assets/images/Evil Charm.png", desc: "Luck up + you feel protected", effect: "+2 luck. Provides immunity to burn, confusion, fear, and poison."),
    Item(id: 633, name: "Dogma", imagePath: "assets/images/Dogma.png", desc: "Ascended", effect: "Grants flight, +2 damage, and increases speed to 1.1. Grants enough health to have 6 full hearts, if possible. Grants the Holy Mantle effect for the room it was obtained in. Automatically given to Isaac after defeating Dogma."),
    Item(id: 634, name: "Purgatory", imagePath: "assets/images/Purgatory.png", desc: "Help from beyond", effect: "While in a room with enemies, spawns red cracks in the ground. Walking over these cracks summons ghosts that launch themselves at enemies and explode."),
    Item(id: 635, name: "Stitches", imagePath: "assets/images/Stitches.png", desc: "Bait and switch", effect: "Passively spawns a familiar that moves in the direction Isaac shoots. On use, Isaac swaps places with the familiar."),
    Item(id: 636, name: "R Key", imagePath: "assets/images/R Key.png", desc: "Time to start over", effect: "Brings Isaac back to the first floor of a new run, retaining all of his items."),
    Item(id: 637, name: "Knockout Drops", imagePath: "assets/images/Knockout Drops.png", desc: "They pack a punch!", effect: "Adds a chance to shoot a fist tear that has extremely high knockback and confuses enemies."),
    Item(id: 638, name: "Eraser", imagePath: "assets/images/Eraser.png", desc: "Erase thy enemy", effect: "Throws an eraser that instantly kills the first monster it hits, and prevents that monster from spawning for the rest of the run. Deals 15 damage to bosses, erasing that boss if fatal. Can only be used once per floor."),
    Item(id: 639, name: "Yuck Heart", imagePath: "assets/images/Yuck Heart.png", desc: "Gross!", effect: "Gives Isaac a Rotten Heart."),
    Item(id: 640, name: "Urn of Souls", imagePath: "assets/images/Urn of Souls.png", desc: "Unleash their sorrow", effect: "Spews a stream of damaging flame. Gains Soul Charges each time an enemy dies, which are used to fuel the urn."),
    Item(id: 641, name: "Akeldama", imagePath: "assets/images/Akeldama.png", desc: "Spill your guts", effect: "While in an uncleared room, Isaac forms a trail of bloody tears that drags behind him, bursting and dealing damage upon contact with enemies."),
    Item(id: 642, name: "Magic Skin", imagePath: "assets/images/Magic Skin.png", desc: "All your desires fulfilled", effect: "Consumes one heart container or two Soul Hearts to spawn an item and give Isaac a broken heart container that can never be filled. If Magic Skin isn't being held after being used, it is very likely to replace any future items."),
    Item(id: 643, name: "Revelation", imagePath: "assets/images/Revelation.png", desc: "Awaken your faith", effect: "Grants flight and two Soul Hearts. In addition to tears, Isaac can charge up and fire a powerful holy laser."),
    Item(id: 644, name: "Consolation Prize", imagePath: "assets/images/Consolation Prize.png", desc: "+1 to lowest stat", effect: "Increases Isaac's lowest stat and drops either 3 Coins, 1 Bomb, or 1 Key depending on what Isaac has the least of."),
    Item(id: 645, name: "Tinytoma", imagePath: "assets/images/Tinytoma.png", desc: "Itching for revenge", effect: "Gives Isaac a Teratoma orbital that blocks shots, deals contact damage, and splits into smaller versions of itself upon taking damage. The smaller versions break into blue spiders."),
    Item(id: 646, name: "Brimstone Bombs", imagePath: "assets/images/Brimstone Bombs.png", desc: "Demon blast +5 bombs", effect: "+5 Bombs. Bombs fire four Brimstone lasers in the cardinal directions upon exploding."),
    Item(id: 647, name: "4.5 Volt", imagePath: "assets/images/4.5 Volt.png", desc: "Beat the juice out of them!", effect: "Active items don't charge when clearing rooms. Instead, they're charged by dealing damage to enemies."),

    Item(id: 649, name: "Fruity Plum", imagePath: "assets/images/Fruity Plum.png", desc: "Bouncy friend", effect: "Gives Isaac a miniature Baby Plum familiar that propels itself diagonally around the room, firing tears behind itself as it travels."),
    Item(id: 650, name: "Plum Flute", imagePath: "assets/images/Plum Flute.png", desc: "Play time!", effect: "Summons the boss Baby Plum to fight for Isaac in the current room."),
    Item(id: 651, name: "Star of Bethlehem", imagePath: "assets/images/Star of Bethlehem.png", desc: "Follow the light", effect: "Spawns a star familiar that slowly travels towards the Boss Room, giving an aura that increases damage and tears and grants homing tears."),
    Item(id: 652, name: "Cube Baby", imagePath: "assets/images/Cube Baby.png", desc: "Kick it!", effect: "Spawns an ice cube familiar that slides around when walked into. It slows and deals contact damage, freezing monsters it kills."),
    Item(id: 653, name: "Vade Retro", imagePath: "assets/images/Vade Retro.png", desc: "Begone!", effect: "While held, enemies leave behind ghosts on death. Stronger enemies leave bigger ghosts. Using the item causes the ghosts to explode and damage nearby enemies, and instantly kills all ghost enemies with less than 50% HP."),
    Item(id: 654, name: "False PHD", imagePath: "assets/images/False PHD.png", desc: "Worse pills + evil up ", effect: "Converts good pills into bad pills and identifies all pills. Bad pills now have secondary positive effects. Grants one Black Heart."),
    Item(id: 655, name: "Spin to Win", imagePath: "assets/images/Spin to Win.png", desc: "Let it rip!", effect: "Gives +0.5 speed and greatly increases the rotation speed and damage of orbitals for 3 seconds. Passively gives Isaac a spinning top orbital that blocks enemy shots and deals contact damage."),

    Item(id: 657, name: "Vasculitis", imagePath: "assets/images/Vasculitis.png", desc: "Clogged enemies", effect: "Enemies explode into tears upon death, which inherit the effects of Isaac's tears."),
    Item(id: 658, name: "Giant Cell", imagePath: "assets/images/Giant Cell.png", desc: "Micro friends", effect: "Causes Isaac to spawn Minisaac familiars when he takes damage, which chase and shoot at nearby enemies."),
    Item(id: 659, name: "Tropicamide", imagePath: "assets/images/Tropicamide.png", desc: "Tear size + range up", effect: "+2.5 Range, Increases tear size."),
    Item(id: 660, name: "Card Reading", imagePath: "assets/images/Card Reading.png", desc: "A link to your future", effect: "Spawns two portals at the beginning of each floor that each lead to either the Boss Room, Treasure Room, Secret Room, or the Shop."),
    Item(id: 661, name: "Quints", imagePath: "assets/images/Quints.png", desc: "They lurk inside", effect: "The first five enemies killed in a room will spawn stationary shooting familiars where they died."),

    Item(id: 663, name: "Tooth and Nail", imagePath: "assets/images/Tooth and Nail.png", desc: "You feel prickly", effect: "Isaac periodically turns into a spiked statue, briefly becoming invulnerable and dealing contact damage."),
    Item(id: 664, name: "Binge Eater", imagePath: "assets/images/Binge Eater.png", desc: "All you can eat", effect: "+1 Red Heart Container and fully heals Isaac. Item pedestals also offer a random food item. Picking up a food item temporarily grants +3.6 damage and permanently boosts 2 stats."),
    Item(id: 665, name: "Guppy's Eye", imagePath: "assets/images/Guppy's Eye.png", desc: "An eye for secrets", effect: "Shows the contents of Chests, Fire Places, Grab Bags, and Shopkeepers before they're opened/destroyed."),

    Item(id: 667, name: "Strawman", imagePath: "assets/images/Strawman.png", desc: "A helping hand", effect: "Spawns a controllable Keeper to fight enemies alongside Isaac, functioning similarly to Jacob and Esau. If Keeper dies, he spawns 5 blue spiders and Strawman is removed from Isaac's inventory."),
    Item(id: 668, name: "Dad's Note", imagePath: "assets/images/Dad's Note.png", desc: "...", effect: "Sends Isaac on an Ascent toward Home. \n Cant spin down"),
    Item(id: 669, name: "Sausage", imagePath: "assets/images/Sausage.png", desc: "All stats up", effect: "Adds a Red Heart Container, fully heals Isaac, and gives +0.2 Speed, +0.5 Damage, +0.5 Tears, +2.5 Range, +0.2 Shot Speed, and +1.0 Luck. Increases the chance of encountering a Devil Room, Angel Room, and/or Planetarium each by 6.9%."),
    Item(id: 670, name: "Options[q]", imagePath: "assets/images/Options[q].png", desc: "There might be options", effect: "If a room gives a reward when cleared, there will be two different rewards to chose from. Taking one causes the other to disappear."),
    Item(id: 671, name: "Candy Heart", imagePath: "assets/images/Candy Heart.png", desc: "Power of love", effect: "Gives Isaac a permanent stat boost whenever he heals with Red Heart pickups."),
    Item(id: 672, name: "A Pound of Flesh", imagePath: "assets/images/A Pound of Flesh.png", desc: "Blood money", effect: "Devil deals now cost Coins, Shop items now cost health, and consumables in shops are free but surrounded by spikes."),
    Item(id: 673, name: "Redemption", imagePath: "assets/images/Redemption.png", desc: "Deliver me from evil", effect: "If Isaac enters a Devil Room but leaves without taking/opening anything in it, he gets +1 Damage and a Soul Heart at the start of the next floor."),
    Item(id: 674, name: "Spirit Shackles", imagePath: "assets/images/Spirit Shackles.png", desc: "Unfinished business", effect: "On death, Isaac's ghost is chained to his dead body and he can continue to fight with 1/2 of a heart, returning to life after 10 seconds. After it activates, it must be recharged by picking up a Soul Heart."),
    Item(id: 675, name: "Cracked Orb", imagePath: "assets/images/Cracked Orb.png", desc: "Shards of knowledge", effect: "Unlocks doors and reveals a random room on the map when Isaac takes damage."),
    Item(id: 676, name: "Empty Heart", imagePath: "assets/images/Empty Heart.png", desc: "It multiplies", effect: "If Isaac has one red heart or fewer at the beginning of a floor, he gains an empty red heart container."),
    Item(id: 677, name: "Astral Projection", imagePath: "assets/images/Astral Projection.png", desc: "The true out-of-body experience!", effect: "Causes Isaac to turn into a ghost if he gets hit while in an uncleared room, giving him flight and spectral tears and blocking the next hit he takes."),
    Item(id: 678, name: "C Section", imagePath: "assets/images/C Section.png", desc: "Fetus shots", effect: "Replaces tears with fetuses that chase down enemies and deal contact damage. Each shot requires a brief charge up."),
    Item(id: 679, name: "Lil Abaddon", imagePath: "assets/images/Lil Abaddon.png", desc: "Abyssal friend", effect: "Spawns a familiar that charges a miniature Maw Of The Void attack while Isaac shoots."),
    Item(id: 680, name: "Montezuma's Revenge", imagePath: "assets/images/Montezuma's Revenge.png", desc: "Oh no...", effect: "While firing, Isaac charges up a short-ranged Brimstone-like diarrhea beam that shoots behind him."),
    Item(id: 681, name: "Lil Portal", imagePath: "assets/images/Lil Portal.png", desc: "It hungers", effect: "A familiar that flies forward, consuming pickups in its path. Each one will increase its size and damage and spawn a Blue Fly. Every four pickups, it opens a portal that leads to an unexplored room."),
    Item(id: 682, name: "Worm Friend", imagePath: "assets/images/Worm Friend.png", desc: "Clingy buddy", effect: "Gives Isaac a Nerve Ending familiar that burrows out of the ground to grab and deal damage to enemies over time."),
    Item(id: 683, name: "Bone Spurs", imagePath: "assets/images/Bone Spurs.png", desc: "Break your enemies", effect: "Enemies spawn floating bones on death, which block shots and damage/repel enemies that touch them."),
    Item(id: 684, name: "Hungry Soul", imagePath: "assets/images/Hungry Soul.png", desc: "Out for blood", effect: "Enemies have a chance to spawn ghosts on death, which chase and deal contact damage to enemies and explode after a short time."),
    Item(id: 685, name: "Jar of Wisps", imagePath: "assets/images/Jar of Wisps.png", desc: "Your faith grows", effect: "Spawns random wisps that shoot various tears, block shots, and deal contact damage. The number of wisps spawned increases every time the jar is used."),
    Item(id: 686, name: "Soul Locket", imagePath: "assets/images/Soul Locket.png", desc: "Power of faith", effect: "Gives Isaac a permanent stat boost whenever he picks up a Soul Heart."),
    Item(id: 687, name: "Friend Finder", imagePath: "assets/images/Friend Finder.png", desc: "Best friends forever!", effect: "Spawns a random friendly monster that mimics Isaac's movements and attacks."),
    Item(id: 688, name: "Inner Child", imagePath: "assets/images/Inner Child.png", desc: "Let him free", effect: "Upon death, Isaac respawns in the current room with half of a Red Heart, a massive size down, and +0.2 Speed."),
    Item(id: 689, name: "Glitched Crown", imagePath: "assets/images/Glitched Crown.png", desc: "?????", effect: "Item pedestals cycle between 5 random items before being picked up."),
    Item(id: 690, name: "Belly Jelly", imagePath: "assets/images/Belly Jelly.png", desc: "Bounce away!", effect: "Causes enemies to bounce off of Isaac, dealing damage to them if they hit an enemy or obstacle. Also gives Isaac the chance to deflect enemy shots."),
    Item(id: 691, name: "Sacred Orb", imagePath: "assets/images/Sacred Orb.png", desc: "Destined for greatness", effect: "Prevents items of quality 0-1 from appearing, and items of quality 2 from appearing 33% of the time. Quest items are unaffected."),
    Item(id: 692, name: "Sanguine Bond", imagePath: "assets/images/Sanguine Bond.png", desc: "He awaits your offering", effect: "Spawns a special set of spikes in the Devil Room. Taking damage from the spikes has a chance to grant a reward."),
    Item(id: 693, name: "The Swarm", imagePath: "assets/images/The Swarm.png", desc: "Infest", effect: "Spawns eight orbital flies, which turn into Blue Flies after they block a shot. Each time a room is cleared, spawns another orbital fly."),
    Item(id: 694, name: "Heartbreak", imagePath: "assets/images/Heartbreak.png", desc: "Eternal sorrow", effect: "Grants 3 broken hearts and +0.25 damage for every broken heart Isaac has. Every otherwise fatal hit will add 2 more until Isaac has nothing but broken hearts."),
    Item(id: 695, name: "Bloody Gust", imagePath: "assets/images/Bloody Gust.png", desc: "May your rage bring haste", effect: "The first 6 times Isaac takes damage on a given floor, he gains Speed and Fire Rate for the rest of the floor, up to a maximum of +1.02 and +3.00, respectively."),
    Item(id: 696, name: "Salvation", imagePath: "assets/images/Salvation.png", desc: "Divine protection", effect: "Gives Isaac a halo that summons beams of light on enemies inside it. The halo grows larger the more times Isaac has taken damage this floor."),
    Item(id: 697, name: "Vanishing Twin", imagePath: "assets/images/Vanishing Twin.png", desc: "He wants revenge", effect: "Spawns a familiar that becomes a weaker clone of the floor's boss when entering the Boss Room. Defeating it drops an extra item. "),
    Item(id: 698, name: "Twisted Pair", imagePath: "assets/images/Twisted Pair.png", desc: "Double trouble!", effect: "Spawns two familiars that float on either side of Isaac and fire tears that use his tears, range, shot speed, and tear effects, and scale with damage."),
    Item(id: 699, name: "Azazel's Rage", imagePath: "assets/images/Azazel's Rage.png", desc: "Ancient power", effect: "Isaac builds rage every time a room is cleared. After four rooms, fires a large Brimstone laser when entering the next room."),
    Item(id: 700, name: "Echo Chamber", imagePath: "assets/images/Echo Chamber.png", desc: "I can see see the future future future", effect: "When Isaac uses a Card, Pill, or Rune, he also uses a copy of the previous three Cards/Pills/Runes he used after picking up Echo Chamber."),
    Item(id: 701, name: "Isaac's Tomb", imagePath: "assets/images/Isaac's Tomb.png", desc: "Buried memories", effect: "Spawns an Old Chest at the start of each floor."),
    Item(id: 702, name: "Vengeful Spirit", imagePath: "assets/images/Vengeful Spirit.png", desc: "Hot blooded", effect: "After taking damage, spawns an orbital wisp that lasts for the entire floor, up to a maximum of six. They fire tears but do not block shots."),
    Item(id: 703, name: "Esau Jr.", imagePath: "assets/images/Esau Jr..png", desc: "Lost brother", effect: "Swaps between the current character and Esau Jr., who has +2 flat damage and flight. Characters have independent items and health. Dying as either character ends the run."),
    Item(id: 704, name: "Berserk", imagePath: "assets/images/Berserk.png", desc: "Rip and tear ", effect: "Isaac goes berserk for 5 seconds, greatly increasing his speed, tears, and damage, but restricting him to using a melee weapon instead of his tears. This item charges by dealing damage to enemies instead of clearing rooms."),
    Item(id: 705, name: "Dark Arts", imagePath: "assets/images/Dark Arts.png", desc: "One with the shadows", effect: "Briefly grants +1 speed and makes Isaac invulnerable and intangible. When the effect ends, destroys all projectiles and damages all enemies Isaac ran through."),
    Item(id: 706, name: "Abyss", imagePath: "assets/images/Abyss.png", desc: "Come forth from the depths", effect: "Destroys all items in the room and gives Isaac a unique locust familiar for each item destroyed. Some items generate locusts with special effects."),
    Item(id: 707, name: "Supper", imagePath: "assets/images/Supper.png", desc: "HP up", effect: "Grants one full Red Heart container. Heals 1 additional Red Heart."),
    Item(id: 708, name: "Stapler", imagePath: "assets/images/Stapler.png", desc: "DMG up", effect: "+1 Damage. Prevents Isaac from firing tears from his left eye, like Cain."),
    Item(id: 709, name: "Suplex!", imagePath: "assets/images/Suplex!.png", desc: "Angel breaker", effect: "Isaac dashes forward and picks up the first enemy hit. After a moment, he slams them into the ground to deal damage and create a circular rock wave."),
    Item(id: 710, name: "Bag of Crafting", imagePath: "assets/images/Bag of Crafting.png", desc: "Make your destiny", effect: "Collects and holds up to 8 pickups, which can then be used to craft an item. The power level of the item is based on the quality of pickups used."),
    Item(id: 711, name: "Flip", imagePath: "assets/images/Flip.png", desc: "Twist of fate", effect: "Item pedestals have a second uninteractable \"ghostly\" item behind them. Activating will flip the real and ghostly items around. If playing as Tainted Lazarus, also flips between them and Dead Tainted Lazarus."),
    Item(id: 712, name: "Lemegeton", imagePath: "assets/images/Lemegeton.png", desc: "Item summoner", effect: "Spawns a wisp representing a random passive item, which orbits Isaac but can be destroyed. While the wisp is alive, Isaac gains that item's effects. Generation uses the current room's item pool 25% of the time, otherwise a random pool."),
    Item(id: 713, name: "Sumptorium", imagePath: "assets/images/Sumptorium.png", desc: "Blood to blood", effect: "Removes half a heart and creates a Clot familiar that mimics Isaac's movements and tear effects. As Tainted Eve, passively converts health into Clots while firing. Activating returns all Clots to health."),
    Item(id: 714, name: "Recall", imagePath: "assets/images/Recall.png", desc: "Come back", effect: "Retrieves the Forgotten's body from any distance. Only available as the Birthright effect for Tainted Forgotten."),
    Item(id: 715, name: "Hold", imagePath: "assets/images/Hold.png", desc: "Saved for later", effect: "Exclusive to Tainted ???. Allows him to save one poop bomb for later, allowing him to gather more over time."),
    Item(id: 716, name: "Keeper's Sack", imagePath: "assets/images/Keeper's Sack.png", desc: "Balls", effect: "Gives range, speed, or damage when buying things from a shop. Also spawns three coins and a Key on pickup."),
    Item(id: 717, name: "Keeper's Kin", imagePath: "assets/images/Keeper's Kin.png", desc: "Under a rock", effect: "Rocks and other Obstacles spawn blue spiders over time while in a room with enemies, and spawn two blue spiders when destroyed."),

    Item(id: 719, name: "Keeper's Box", imagePath: "assets/images/Keeper's Box.png", desc: "Portable shop", effect: "Spawns a random shop item or pickup which can be bought for its normal price."),
    Item(id: 720, name: "Everything Jar", imagePath: "assets/images/Everything Jar.png", desc: "Anything is possible", effect: "Spawns certain pickups based on how many charges it has. If fully charged, instead has one of many unique effects."),
    Item(id: 721, name: "TMTRAINER", imagePath: "assets/images/TMTRAINER.png", desc: "ZZaZZ ZZdZZiZZmZZhZZ ZZvZZ ZZoZZ ZZ ZZsZZlZZhZZsZZoZZaZZiZZ", effect: "Causes all future items to be \"glitched\", granting them completely random effects."),
